import React, { useEffect, useState } from 'react';
import { getProducts } from '../services/api';
import Typography from '@mui/material/Typography';
import Box from '@mui/material/Box';
import Grid from '@mui/material/Grid';
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import CardActions from '@mui/material/CardActions';
import Button from '@mui/material/Button';

export default function Store() {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    let mounted = true;
    getProducts().then(res => {
      if (mounted) setProducts(res.data || []);
    }).catch(() => {});
    return () => { mounted = false };
  }, []);

  return (
    <Box>
      <Typography variant="h4">Store</Typography>
      <Typography sx={{ mt: 1 }}>Bienvenido a la tienda. Agrega productos al carrito desde aqu√≠.</Typography>
      <Grid container spacing={2} sx={{ mt: 2 }}>
        {products.map(p => (
          <Grid item xs={12} sm={6} md={4} key={p.id || p._id}>
            <Card>
              <CardContent>
                <Typography variant="h6">{p.name}</Typography>
                <Typography color="text.secondary">{p.description}</Typography>
                <Typography sx={{ mt: 1 }}>${p.price}</Typography>
              </CardContent>
              <CardActions>
                <Button size="small">Add to cart</Button>
              </CardActions>
            </Card>
          </Grid>
        ))}
      </Grid>
    </Box>
  );
}
